uint32_t anim_timer;
uint32_t anim_sleep_time;
uint8_t  current_idle_frame;
uint8_t current_tap_frame;
uint32_t max_wpm;
uint32_t max_wpm_timer;

void animation(void) {
    if (get_current_wpm() <= IDLE_SPEED) {
        current_idle_frame = (current_idle_frame + 1) % IDLE_FRAMES;
        oled_write_raw(CAT_IDLE_FRAMES[abs((IDLE_FRAMES - 1) - current_idle_frame)], ANIM_SIZE);
    }
    if (get_current_wpm() > IDLE_SPEED && get_current_wpm() < TAP_SPEED) {
        oled_write_raw(CAT_PREP_FRAMES[0], ANIM_SIZE);
    }
    if (get_current_wpm() >= TAP_SPEED) {
        current_tap_frame = (current_tap_frame + 1) % TAP_FRAMES;
        oled_write_raw(CAT_TAP_FRAMES[abs((TAP_FRAMES - 1) - current_tap_frame)], ANIM_SIZE);
    }
}

void render_bongocat(void) {
    if (get_current_wpm() != 0) {
        anim_sleep_time = timer_read32();
    }
    if (timer_elapsed32(anim_sleep_time) > OLED_SLEEP_TIME) {
        oled_off();
    }

    if (timer_elapsed32(anim_timer) > ANIM_FRAME_DURATION) {
        anim_timer = timer_read32();
        animation();
    }
}

